mapper.select:
select compid, TaskId, VehicleID
from PT_JumpVehicleSendDetail
where compid = #{compid}
  and taskid = #{taskId}
   and DeleteMark=0

select PersonalName
from VM_PersonalInfo
where compid = #{compid}
  and PersonalCode = #{driverCode}

select
personalName
from VM_PersonalInfo
where compid = #{compid}
  and PersonalCode = #{driverCode}
