mapper.select:
select compid, TaskId, VehicleID
from PT_JumpVehicleSendDetail
where compid = #{compid}
  and taskid = #{taskId}
   and DeleteMark=0

select PersonalName
from VM_PersonalInfo
where compid = #{compid}
  and PersonalCode = #{driverCode}

select
personalName
from VM_PersonalInfo
where compid = #{compid}
  and PersonalCode = #{driverCode}

select tsi.ID, tsi.VehicleStatus, tsi.VehicleID, tsi.PersonalCode, vm.PersonalName, tsi.VehiceNumber, tp.Placing, tp.TaskId, tp.eppCode, tp.builderCode, tsi.ThreeProduceNum, tsi.TaWeight, tsi.GrWeight, tsi.NetWeight, tsi.SendTime, tsi.totalNum, tsi.NumberOfSignings, tsi.SaleNum, e.EPPName, b.BuilderName, tp.StgId, tp.slump, ps.PI_TypeName placeStyleName, tsi.upStatus, tsi.InvoiceType, vs.PI_TypeName VehicleStatusName, it.PI_TypeName invoiceTypeName, tsi.VehicleID_Jump JumpVehicleID, tsi.ReceiptMan receiptMan, tsi.SaleFileImage saleFileImage, tp.Attribute as attribute from PT_TaskSaleInvoice tsi left join PT_TaskPlan tp on tsi.compid = tp.compid and tsi.TaskId = tp.TaskId left join DD_PublicInfo ps on ps.PI_Class = 24 and ps.PI_Values = tp.PlaceStyle and tsi.compid = ps.compid left join SM_EPPInfo e on tp.EPPCode = e.EPPCode and e.compid = tsi.compid left join SM_BuilderInfo b on tp.BuilderCode = b.BuilderCode and b.compid = tsi.compid left join DD_PublicInfo vs on vs.PI_Class = 22 and tsi.VehicleStatus=vs.PI_Values and tsi.compid =vs.compid left join DD_PublicInfo it on it.PI_Class = 51 and tsi.InvoiceType=it.PI_Values and tsi.compid = it.compid left join VM_PersonalInfo vm on tsi.PersonalCode =vm.PersonalCode and tsi.compid =vm.compid left join VM_VehicelManagement vv on tsi.compid =vv.compid and tsi.VehicleID =vv.CarID where tsi.compid=#{compid} and tsi.PersonalCode=#{driverCode} and tsi.InvoiceType not in('1','2','6') and tsi.recStatus = 1 and (tsi.InvoiceType !=4 and vv.VehicleStatus !=3)
and tsi.ID like #{id}
and tp.EPPCode = #{eppCode}
and tsi.upStatus = #{upStatus}
and tp.builderCode = #{builderCode}
and tp.placing like '%'+#{placing}+'%'
and tsi.SendTime >= #{beginTime}
and tsi.SendTime <= #{endTime}

select count(*) as totalCarNum, sum(SaleNum) as produceNum, sum(tsi.Distance) as distance
from PT_TaskSaleInvoice tsi
left join PT_TaskPlan tp on tsi.compid = tp.compid and tsi.TaskId = tp.TaskId
left join VM_VehicelManagement vv on tsi.compid =vv.compid and tsi.VehicleID =vv.CarID
where tsi.compid=#{compid}
and tsi.PersonalCode=#{driverCode}
and tsi.InvoiceType not in('1','2','6')
and tsi.recStatus = 1
and (tsi.InvoiceType !=4 and vv.VehicleStatus !=3)

    and tp.EPPCode = #{eppCode}


    and tsi.upStatus = #{upStatus}


    and tp.builderCode = #{builderCode}


    and tp.placing like '%'+#{placing}+'%'


    and tsi.SendTime   >=   #{beginTime}


    and tsi.SendTime  <=  #{endTime}
