mapper.update:
update LM_TaskUnrealFormula
set VerifyStatus=#{verifyStatus},
    VerifyId = '0225',
    VerifyTime = #{currentTime},
    UpDownMark = 0,
    UpDown = 0
where compid = #{compid}
  and TaskId = #{taskId}
  and FormulaCode = #{formulaCode}
  and StirId = #{stirId}

update LM_TaskTheoryFormula
set VerifyStatus=#{verifyStatus},
    VerifyId = '0225',
    VerifyTime = #{currentTime},
    UpDownMark = 0,
    UpDown = 0
where compid = #{compid}
  and TaskId = #{taskId}
  and FormulaCode = #{formulaCode}
  and StirId = #{stirId}

mapper.select:
select StirId, tf.VerifyStatus, tf.TaskId from LM_TaskTheoryFormula tf left join PT_TaskPlan pt on tf.compid = pt.compid and tf.TaskId = pt.TaskId left join LM_TheoryFormula lm on tf.TheoryFormulaCode = lm.TheoryFormulaCode and tf.compid = lm.compid where (tf.compid = #{compid}) and (tf.TaskId in
#{taskId}
) group by StirId, tf.VerifyStatus, tf.TaskId

select * from LM_TheoryFormula
where compid=#{compid}

    and IdentifyNumber = #{IdentifyNumber}


    and IdentifyNumber = 2


    and (FormulaCheckCode like '%'+#{searchWord}+'%' or StgId like'%'+#{searchWord}+'%')


    and CreateTime   >=  #{beginTime}


    and CreateTime   <  #{endTime}

select * from LM_TheoryFormula
where compid=#{compid}
    and FormulaCheckCode=#{formulaCheckCode}
    and TheoryFormulaCode=#{theoryFormulaCode}
    and StgId=#{stgId}
    and IdentifyNumber=#{identifyNumber}

select top 1 * from LM_TheoryFormula
where compid=#{compid}
    and TheoryFormulaCode=#{theoryFormulaCode}
    and StgId=#{stgId}
    and IdentifyNumber=#{identifyNumber}

select top 1 * from LM_TaskUnrealFormula
where compid=#{compid}
and TaskId=#{taskId}
and StirId=#{stirId}

select top 1 * from LM_TaskTheoryFormula
where compid=#{compid}
and TaskId=#{taskId}
and StirId=#{stirId}

select top 1 *
from LM_TaskProduceFormula
where compid=#{compid}
and TaskId=#{taskId}
and StirId=#{stirId}

select top 1 *
from LM_TaskProduceFormula
where compid = #{compid}
    and TaskId = #{taskId}
    and StirId=#{stirId}

select top 1 *
from LM_TaskUnrealFormula
where compid = #{compid}
    and TaskId = #{taskId}
    and StirId=#{stirId}

select top 1 *
from LM_TaskTheoryFormula
where compid = #{compid}
    and TaskId = #{taskId}
    and StirId=#{stirId}
