mapper.select:
select w.RequestNumber, d1.PI_TypeName RequestMode, d2.PI_TypeName RequestStatus, u.name, w.Buyer, w.VerifyIdOne, w.VerifyStatusOne, w.VerifyTimeOne, w.AuditResultOne, w.VerifyIdTwo, w.VerifyStatusTwo, w.VerifyTimetwo, w.AuditResultTwo, w.VerifyIdThree, w.VerifyStatusThree, w.VerifyTimeThree, w.AuditResultThree, w.VerifyIdFour, w.VerifyStatusFour, w.VerifyTimeFour, w.AuditResultFour, w.VerifyIdFive, w.VerifyStatusFive, w.VerifyTimeFive, w.AuditResultFive, w.VerifyIdSix, w.VerifyStatusSix, w.VerifyTimeSix, w.AuditResultSix, w.RequestDep, w.GoodsName, w.Specification, w.Num, w.Remarks, w.CreateTime, w.Amount from WM_ConFigureApply w left join DD_PublicInfo d1 on d1.PI_Class = '76' and w.RequestMode = d1.PI_Values and w.compid =d1.compid left join DD_PublicInfo d2 on d2.PI_Class = '75' and w.RequestStatus = d2.PI_Values and w.compid =d2.compid left join User_dept u on w.compid =u.compid and w.Department =u.deptid where w.compid =#{compid}
and w.CreateTime >= #{beginTime}
and w.CreateTime <= #{endTime}
and w.goodsName like '%'+#{goodsName}+'%'
and w.buyer = #{buyer}
and w.specification like '%'+#{specification}+'%'
and w.department = #{department}
and w.verifyStatusOne = #{verifyStatusOne}
and w.requestNumber like '%'+#{requestNumber}+'%'
and w.requestStatus = #{requestStatus}
and w.requestDep like '%'+#{requestDep}+'%'

mapper.update:
UPDATE WM_ConFigureApply
set

    RequestStatus = #{requestStatus}


    , VerifyIdOne = #{verifyIdOne}

, VerifyStatusOne = #{verifyStatusOne}


    , VerifyTimeOne = #{verifyTimeOne}


    , AuditResultOne = #{auditResultOne}



where compid = #{compid}
and RequestNumber = #{requestNumber}

UPDATE WM_ConFigureApply
set
    RequestStatus  = #{requestStatus}
    , VerifyIdOne  = #{verifyIdOne}
    , VerifyStatusOne  = #{verifyStatusOne}
    , VerifyTimeOne  = #{verifyTimeOne}
    , AuditResultOne  = #{auditResultOne}
where compid = #{compid}
and RequestNumber = #{requestNumber}
