mapper.update:
update MP_StockIn set
compid=#{compid}

    ,GrWeight=#{grWeight}


    ,TaWeight=#{taWeight}


    ,ClWeight=#{clWeight}


    ,netWeight=#{netWeight}


    ,deductNum=#{deductNum}


    , deduct=#{deduct}


    ,remarks=#{remarks}


    ,firstTime=#{firstTime}


    ,secondTime=#{secondTime}


    , sup_ExFactory=#{supExFactory}


    , SupCode=#{supCode}


    ,StkCode=#{stkCode}


    ,SaleType=#{saleType}


    ,vehicleID=#{vehicleId}


    ,matCode=#{matCode}


    ,OPER1=#{opid}


    ,OPER2=#{opids}


    ,Sup_OilNum=#{supOilNum}


    ,StI_Times=#{stlTimes}

where compid=#{compid}
and stICode=#{stICode}

mapper.select:
select sum(si.ClWeight) as clWeight from MP_StockIn si left join dbo.MP_Supplier ms on si.SupCode = ms.SupCode and si.compid = ms.compid left join MP_MaterialInfor mt on si.MatCode = mt.MatCode and si.compid = mt.compid where si.compid = #{compid} and si.RecStatus=1
and vehicleId like '%'+#{vehicleId}+'%'
and Sup_Name like '%'+#{supName}+'%'
and MatName like '%'+#{matName}+'%'
and si.SecondTime between #{beginTime} and #{endTime}
and saleType=#{saleType}
