mapper.select:
select *
from De_StockCompare
where compid = #{compid}
  and StirId = #{stirId}

select Sto_Name as stoName from MP_Stock where compid=#{compid}
and Sto_Name like '%'+#{stoName}+'%'
group by Sto_Name

select MatCode,MatName,MatParent from MP_MaterialInfor where compid=#{compid}
and Sto_Name like '%'+#{searchWords}+'%'
order by MatName

select ms.StkCode,ms.Sto_Name,StirId as stirId,ms.Sto_Curqty as stoCurqty from MP_Stock ms where ms.compid=#{compid}
and ms.Sto_Name like '%'+#{searchWords}+'%'
order by StirId

select StkCode, Sto_Name, StirId
from MP_Stock
where compid = #{compid} and StirId = #{stirId}
