mapper.select:
select tsi.ID, vv.VehicleStatus, tsi.VehicleID, (tsi.SaleNum - tsi.ReturnNum - tsi.RemnantNum -tsi.ScrapNum) SaleNum, tsi.NumberOfSignings numberOfSignings, tsi.PersonalCode, vm.PersonalName, tsi.VehiceNumber, tp.TaskId, tsi.ThreeProduceNum, tsi.TaWeight, tsi.GrWeight, tsi.NetWeight, e.EPPName, b.BuilderName, tp.StgId, tsi.Arrive_STTime arriveSttime, tp.Slump, tp.Placing, tp.Attribute, ps.PI_TypeName placeStyleName, upStatus, vs.PI_TypeName VehicleStatusName, it.PI_TypeName invoiceTypeName, tsi.Leave_STTime leaveTime from PT_TaskSaleInvoice tsi left join PT_TaskPlan tp on tsi.compid = tp.compid and tsi.TaskId = tp.TaskId left join DD_PublicInfo ps on ps.PI_Class = 24 and ps.PI_Values = tp.PlaceStyle and tsi.compid = ps.compid left join SM_EPPInfo e on tp.EPPCode = e.EPPCode and e.compid = tsi.compid left join SM_BuilderInfo b on tp.BuilderCode = b.BuilderCode and b.compid = tsi.compid left join DD_PublicInfo vs on vs.PI_Class = 22 and tsi.VehicleStatus=vs.PI_Values and tsi.compid =vs.compid left join DD_PublicInfo it on it.PI_Class = 51 and tsi.InvoiceType=it.PI_Values and tsi.compid = it.compid left join VM_PersonalInfo vm on tsi.PersonalCode =vm.PersonalCode and tsi.compid =vm.compid left join VM_VehicelManagement vv on tsi.compid =vv.compid and tsi.VehicleID =vv.CarID where tsi.compid=#{compid} and tsi.RecStatus=1 and tsi.InvoiceType!=6
and tsi.ID like '%'+#{invoiceId}+'%'
and tsi.SendTime >= #{beginTime}
and tsi.SendTime <= #{endTime}
and tsi.Leave_STTime >= #{beginTime}
and tsi.Leave_STTime <= #{endTime}
and tp.EPPCode = #{eppCode}
and (tsi.upStatus=0 or tsi.upStatus is null) and tsi.upStatus = #{upStatus}
and tp.builderCode = #{builderCode}
and tp.taskStatus = #{taskStatus}
and tp.taskId like '%'+#{taskId}+'%'
and tp.placing like '%'+#{placing}+'%'

mapper.update:
UPDATE PT_TaskSaleInvoice
set NumberOfSignings       = #{numberOfSignings},
    upStatus      = 1,
    ReceiptMan    = #{opid},
    saleFileImage =#{saleFileImage},
    signingTime   = #{signingTime}
where compid = #{compid}
  and id = #{id}

mapper.select:
select sum(tsi.SaleNum - tsi.ReturnNum - tsi.RemnantNum -tsi.ScrapNum) saleNum,
sum(NumberOfSignings) signNum
from PT_TaskSaleInvoice tsi
left join PT_TaskPlan tp on tsi.compid = tp.compid and tsi.TaskId = tp.TaskId
where tsi.compid=#{compid}
and tsi.RecStatus=1
and tsi.InvoiceType!=6

    and tsi.ID like '%'+#{id}+'%'




        and tsi.SendTime   >=   #{beginTime}


        and tsi.SendTime  <=  #{endTime}




        and tsi.Leave_STTime   >=   #{beginTime}


        and tsi.Leave_STTime  <=  #{endTime}





    and tp.EPPCode = #{eppCode}


    and (tsi.upStatus=0 or tsi.upStatus is null)
    and tsi.upStatus = #{upStatus}


    and tp.builderCode = #{builderCode}


    and tp.taskStatus = #{taskStatus}


    and tp.taskId like '%'+#{taskId}+'%'


    and tp.placing like '%'+#{placing}+'%'
