select tsi.TaskId, bi.BuilderCode, BuilderName, tp.EPPCode, EPPName, VehicleID, tsi.TotalNum num, tsi.VehiceNumber carNum, Leave_STTime leaveTime, Arrive_STTime arriveTime, concreteMark, placing, ps.PI_TypeName placeStyleName, SaleNum, tp.StgId as stgId, vmpi.personalName as personalName from PT_TaskSaleInvoice tsi left join PT_TaskPlan tp on tsi.compid = tp.compid and tp.TaskId = tsi.TaskId left join SM_EPPInfo epp on tp.EPPCode = epp.EPPCode and tp.compid = epp.compid left join SM_BuilderInfo bi on tp.BuilderCode = bi.BuilderCode and bi.compid = tp.compid left join DD_PublicInfo ps on ps.PI_Class = 24 and ps.PI_Values = tp.PlaceStyle and tsi.compid =ps.compid left join VM_PersonalInfo vmpi on vmpi.PersonalCode = tsi.PersonalCode and tsi.compid=vmpi.compid where bi.compid = #{compid} and (tsi.InvoiceType not in (1, 2, 6)) and (tsi.VehicleStatus not in ('8', '9', '10', '11'))
and tp.eppCode=#{eppCode}
and tp.placing like '%'+#{placing}+'%'
and VehicleID =#{vehicleId}
and vmpi.personalName like '%'+#{personalName}+'%'
and tp.TaskId like '%'+#{taskId}+'%'
and tsi.SendTime >= #{beginTime}
and tsi.SendTime <= #{endTime}
group by tsi.TaskId, tsi.TotalNum, VehiceNumber, bi.BuilderCode, BuilderName, tp.EPPCode, EPPName, VehicleID, tp.compid, Leave_STTime, concreteMark, placing, ps.PI_TypeName, Arrive_STTime, SaleNum, SendTime, ReturnNum, RemnantNum, InvoiceType, VehicleStatus,tp.StgId,personalName order by tsi.TaskId,SendTime

select count(tsi.VehiceNumber)carNum, sum(SaleNum) num, avg(tsi.Distance) distance, tsi.VehicleID vehicleId from PT_TaskSaleInvoice tsi left join PT_TaskPlan tp on tsi.compid = tp.compid and tp.TaskId = tsi.TaskId left join SM_EPPInfo epp on tp.EPPCode = epp.EPPCode and tp.compid = epp.compid left join SM_BuilderInfo bi on tp.BuilderCode = bi.BuilderCode and bi.compid = tp.compid left join VM_PersonalInfo vm on tsi.compid = vm.compid and tsi.PersonalCode = vm.PersonalCode where tsi.compid = #{compid} and ( tsi.InvoiceType not in(1,2,6) ) and (tsi.VehicleStatus not in ('8','9','10','11')) and VehiceNumber <> '0'
and tp.eppCode=#{eppCode}
and tp.placing like '%'+#{placing}+'%'
and vehicleId like '%'+#{vehicleId}+'%'
and vm.personalName like '%'+#{personalName}+'%'
and tp.taskId like '%'+#{taskId}+'%'
and tsi.SendTime >= #{beginTime}
and tsi.SendTime <= #{endTime}
group by tsi.VehicleID order by num desc

