and tp.eppCode=#{eppCode}
and tp.placing like '%'+#{placing}+'%'
and vehicleId like '%'+#{vehicleId}+'%'
and vm.personalName like '%'+#{personalName}+'%'
and tp.taskId like '%'+#{taskId}+'%'
and tsi.SendTime >= #{beginTime}
and tsi.SendTime <= #{endTime}

