select epp.EPPName eppName, vm.FirstTime firstTime, vm.GrWeight grWeight, vm.NetWeight netWeight, vm.SecondTime secondTime, vm.StgId stgId, vm.TaWeight taWeight, vm.TaskId taskId, vm.VehicleID vehicleId, vm.WeightFang weightFang, dd.PI_TypeName weightTypeName, us.empname empName from VM_VehicleWeight vm left join User_employee us on vm.compid = us.compid and vm.OPER1 =us.OpId left join PT_TaskPlan tp on vm.compid = tp.compid and tp.TaskId = vm.TaskId left join SM_EPPInfo epp on tp.EPPCode = epp.EPPCode and tp.compid = epp.compid left join DD_PublicInfo dd on dd.PI_Class ='53' and vm.WeightType =dd.PI_Values and vm.compid =dd.compid where vm.compid = #{compid}
and vm.WeightType=#{weightType}
and tp.eppCode = #{eppCode}
and us.empname like '%'+#{empNameb}+'%'
and vm.SecondTime >= #{beginTime}
and vm.SecondTime <= #{endTime}

