select jvs.VehicleID, count(jvs.VehicleID) carNum, sum(tsi.SaleNum) - sum(tsi.ReturnNum) - sum(tsi.ScrapNum)-sum(tsi.RemnantNum) num from PT_JumpVehicleSendDetail jvs left join PT_TaskSaleInvoice tsi on jvs.compid = tsi.compid and jvs.TaskId = tsi.TaskId and jvs.VehicleID = tsi.VehicleID_Jump left join VM_VehicelManagement vm on jvs.compid = vm.compid and jvs.VehicleID = vm.CarID left join DD_PublicInfo dd1 on vm.VehicleClass = dd1.PI_Values and vm.compid = dd1.compid and dd1.PI_Class = 33 left join VM_PersonalWorkClass pc on vm.compid = pc.compid and vm.CarID = pc.VehicleID left join PT_TaskPlan pt on jvs.compid = pt.compid and jvs.TaskId = pt.TaskId left join VM_PersonalInfo mm on jvs.PersonalCode = mm.PersonalCode and jvs.compid = mm.compid left join DD_PublicInfo dd on tsi.compid = dd.compid and tsi.PlaceStyle = dd.PI_Values and dd.PI_Class = 24 where jvs.compid =#{compid} and VehicleID_Jump <> '' and VehicleID_Jump is not null and jvs.VehicleID <> '' and jvs.VehicleID is not null and (InvoiceType in (3, 4, 5) and tsi.VehicleStatus = 3)
and tsi.SendTime >= #{beginTime}
and tsi.SendTime <= #{endTime}
and pt.eppCode=#{eppCode}
and mm.personalname like '%'+#{personalName}+'%'
and tsi.stirid like '%'+#{stirId}+'%'
and tsi.TaskId like '%'+#{taskId}+'%'
and tsi.vehicleID like '%'+#{vehicleId}+'%'
group by jvs.VehicleID order by num desc

