select.if:
select convert(varchar(10),tsi.sendtime,120) as sendTime, tsi.taskId, sb.builderName, sm.eppName, pt.placing, count(pt.TotalVehicleNum )carNum, pt.stgId, sum(tsi.produceNum) produceNum, sum(tsi.SaleNum - tsi.ReturnNum - tsi.SeparateNum - tsi.ScrapNum) saleNum from PT_TaskSaleInvoice tsi left join PT_TaskPlan pt on tsi.compid =pt.compid and tsi.TaskId =pt.TaskId left join SM_EPPInfo sm on pt.compid =sm.compid and pt.EPPCode =sm.EPPCode left join SM_BuilderInfo sb on pt.compid =sb.compid and pt.BuilderCode =sb.BuilderCode where tsi.compid = #{compid} and (VehiceNumber > '0') and (VehicleStatus = '3') and tsi.InvoiceType != 6
and pt.eppCode=#{eppCode}
and pt.placing like '%'+#{placing}+'%'
and tsi.taskId like '%'+#{taskId}+'%'
and pt.stgid like '%'+#{stgId}+'%'
and tsi.sendTime >= #{beginTime}
and tsi.sendTime <= #{endTime}
Group By convert(varchar(10),tsi.sendTime,120),tsi.taskId, eppname, pt.buildercode, buildername, placing, stgid ORDER BY convert(varchar(10),tsi.sendTime,120),tsi.taskId

select convert(varchar(10),tsi.Leave_STTime,120) as sendTime, tsi.taskId, sb.builderName, sm.eppName, pt.placing, count(pt.TotalVehicleNum )carNum, pt.stgId, sum(tsi.produceNum) produceNum, sum(tsi.SaleNum - tsi.ReturnNum - tsi.SeparateNum - tsi.ScrapNum) saleNum from PT_TaskSaleInvoice tsi left join PT_TaskPlan pt on tsi.compid =pt.compid and tsi.TaskId =pt.TaskId left join SM_EPPInfo sm on pt.compid =sm.compid and pt.EPPCode =sm.EPPCode left join SM_BuilderInfo sb on pt.compid =sb.compid and pt.BuilderCode =sb.BuilderCode where tsi.compid = #{compid} and (VehiceNumber > '0') and (VehicleStatus = '3') and tsi.InvoiceType != 6
and pt.eppCode=#{eppCode}
and pt.placing like '%'+#{placing}+'%'
and tsi.taskId like '%'+#{taskId}+'%'
and pt.stgid like '%'+#{stgId}+'%'
and tsi.Leave_STTime >= #{beginTime}
and tsi.Leave_STTime <= #{endTime}
Group By convert(varchar(10),tsi.Leave_STTime,120),tsi.taskId, eppname, pt.buildercode, buildername, placing, stgid ORDER BY convert(varchar(10),tsi.Leave_STTime,120),tsi.taskId

mapper.select:
select
sum(tsi.SaleNum - tsi.ReturnNum - tsi.SeparateNum - tsi.ScrapNum) saleNumList
from PT_TaskSaleInvoice tsi
left join PT_TaskPlan pt on tsi.compid =pt.compid and tsi.TaskId =pt.TaskId
left join SM_EPPInfo sm on pt.compid =sm.compid and pt.EPPCode =sm.EPPCode
left join SM_BuilderInfo sb on pt.compid =sb.compid and pt.BuilderCode =sb.BuilderCode
where tsi.compid = #{compid}
and (VehiceNumber > '0')
and (VehicleStatus = '3')
and tsi.InvoiceType != 6

    and pt.eppCode=#{eppCode}


    and pt.placing like '%'+#{placing}+'%'


    and pt.taskId like '%'+#{taskId}+'%'


    and pt.stgid like '%'+#{stgId}+'%'



        and tsi.SendTime  >=   #{beginTime}


        and tsi.SendTime  <=  #{endTime}




        and tsi.Leave_STTime  >=   #{beginTime}


        and tsi.Leave_STTime  <=  #{endTime}

select sum(PanNum) from PT_ProduceConsume where RecStatus=1
and compid=#{compid}

    and ProduceTime  >=   #{beginTime}


    and ProduceTime  <=  #{endTime}
